UPDATE `sgulot_kotrot` SET `verse_text` = 'תַּפּוּחֵי זָהָב בְּמַשְׂכִּיּוֹת כָּסֶף = \r\nדָּבָר דָּבֻר עַל אָפְנָיו.' WHERE `book` = 'משל' AND `chapter_number` = 25 AND `verse_number` = 11 AND `verse_text` = 'תַּפּוּחֵי זָהָב בְּמַשְׂכִּיּוֹת כָּסֶף = דָּבָר דָּבֻר עַל אָפְנָיו.' AND `tirgum` = '<p>\r\n<strong>תפוחים</strong>   (קישוטים כדוריים) יקרים, העשויים מ<strong>זהב</strong>, יש לשים במשכיות (כלים) יקרים, העשויים מ<strong>כסף</strong>;&#160;&#160; כך גם, \r\n<strong>דבר</strong>-חכמה יקר וחשוב צריך להיות \r\n<strong>דבור</strong>   (נאמר) \r\n<strong>באופן</strong> הראוי, נאה ומנוסח היטב, כך שיתקבל על לב השומעים.</p>' AND `ecot` = '<br />' AND `kotrt` = 'להגיד דברי חכמה בצורה נעימה' AND 1 LIMIT 1;

