UPDATE `sgulot_kotrot` SET `verse_text` = 'בְּאֹרֶךְ אַפַּיִם יְפֻתֶּה קָצִין, \r\nוְלָשׁוֹן רַכָּה תִּשְׁבָּר גָּרֶם.' WHERE `book` = 'משל' AND `chapter_number` = 25 AND `verse_number` = 15 AND `verse_text` = 'בְּאֹרֶךְ אַפַּיִם יְפֻתֶּה קָצִין,וְלָשׁוֹן רַכָּה תִּשְׁבָּר גָּרֶם.' AND `tirgum` = '<p>כשנוהגים \r\n<strong>בארך אפיים</strong>  (סבלנות ורגיעה), ולא מתעצבנים בחזרה, אפשר \r\n<strong>לפתות</strong>  גם \r\n<strong>קצין</strong>  כועס לסלוח לנו; &#160; וכן, כשמדברים \r\n<strong>בלשון רכה</strong>   ונעימה, אפשר \r\n<strong>לשבור</strong>   את הכעס הקשה כמו \r\n<strong>גרם</strong>   (עצם).</p> \r\n<p>/ \r\n<strong>כשקצין</strong>   כועס עלינו, האסטרטגיה הטובה ביותר היא \r\n<strong>לפתות</strong>   אותו לנהוג \r\n<strong>באורך אפיים</strong>, לדחות את ביצוע העונש ולא לבטלו לגמרי; &#160; אם נדבר \r\n<strong>בלשון רכה</strong>, המביעה הסכמה עם כעסו וקבלה חלקית של טענותיו, נוכל  \r\n<strong>לשבור</strong>   את \r\n<strong>הגרם</strong>, את עצם ועיקר הכעס.</p>' AND `ecot` = '<br />' AND `kotrt` = 'בארך אפים יפתה קצין, ולשון רכה תשבר גרם' AND 1 LIMIT 1;

