UPDATE `sgulot_kotrot` SET `verse_text` = '= הֹקַר רַגְלְךָ מִבֵּית רֵעֶךָ, \r\nפֶּן יִשְׂבָּעֲךָ וּשְׂנֵאֶךָ.' WHERE `book` = 'משל' AND `chapter_number` = 25 AND `verse_number` = 17 AND `verse_text` = '= הֹקַר רַגְלְךָ מִבֵּית רֵעֶךָ,\r\nפֶּן יִשְׂבָּעֲךָ וּשְׂנֵאֶךָ.' AND `tirgum` = '<p>\r\n- <strong>הקר</strong>    (קרר, צנן) את \r\n<strong>רגלך</strong>, אל תלך בתדירות גבוהה מדי אל \r\n<strong>בית רעך</strong>, בקר אצלו רק כשאתה רואה שהוא רעב לביקור, \r\n<strong>פן</strong>    הוא \r\n<strong>ישבע</strong>    מהקשר עמך ויתחיל לשנוא אותך.</p>' AND `ecot` = '<br />' AND `kotrt` = 'ביאור:משלי כה17' AND 1 LIMIT 1;

