UPDATE `sgulot_kotrot` SET `verse_text` = 'מֵפִיץ, וְחֶרֶב, וְחֵץ שָׁנוּן = \r\nאִישׁ עֹנֶה בְרֵעֵהוּ עֵד שָׁקֶר.' WHERE `book` = 'משל' AND `chapter_number` = 25 AND `verse_number` = 18 AND `verse_text` = 'מֵפִיץ, וְחֶרֶב, וְחֵץ שָׁנוּן =\r\nאִישׁ עֹנֶה בְרֵעֵהוּ עֵד שָׁקֶר.' AND `tirgum` = '<p>כמו פטיש \r\n<strong>מפוצץ</strong>  סלעים,  העלול לפגוע גם במי שמפעיל אותו; כמו \r\n<strong>חרב</strong>, הפוגעת בקרובים לה; וכמו \r\n<strong>חץ שנון</strong>, הפוגע ברחוקים - כך הוא \r\n<strong>איש</strong>   אשר \r\n<strong>עונה</strong>   (מעיד)\r\n<strong> ברעהו עדות שקר</strong>: הוא מזיק גם לעצמו, גם לשומעים, וגם לרעהו שהעדות נאמרה עליו.</p>' AND `ecot` = '<br />' AND `kotrt` = 'לישנא תליתאי' AND 1 LIMIT 1;

