		INSERT INTO tguvot (parent,body,userid,username,created_at) VALUES (
		'tnk1/messages/sig_9sfr_84.html',
		'<p>אני לא חושב כך. הן חריפות, אך לא מדי. זכותי להביע את דעתי. ובאמת נמשיך הלאה</p>',
		'115183744128479087177',
		'hagai hoffer',
