		INSERT INTO tguvot (parent,body,userid,username,created_at) VALUES (
		'tnk1/sofrim/meroz/cron_glut.html',
		'<p>רוב האמונות העתיקות התייחסו לאל שלהם כאלים מקומיים ולכן לכל אל יש גם תפקיד . ראה מלכים א\' פרק כ\' .אחאב הכה את הצבא הארמי  ואומרים למלך  הארמי:</p><p>&quot;ועבדי מלך-ארם אמרו אליו, אלוהי הרים אלוהיהם, על-כן, חזקו ממנו; ואולם, נילחם איתם במישור--אם-לא נחזק, מהם. </p><p>אחנאתון הפרעה המהפכן הנהיג לראשונה את פולחן האל היחיד &quot;אתן&quot;  אל השמש כיוון שהשמש נמצאת בכול מקום מכאן שהשמש היא האל החשוב והיתר כפופים לו .</p>',
		'115490415335975282863',
		'קובי מרוז',
		'2015-12-31 11:24:40'
		)
		;

