		INSERT INTO tguvot (parent,body,userid,username,created_at) VALUES (
		'tnk1/ktuv/mjly/mvne.html',
		'<p> אראל מדוע אתה חושב שאנשי חזקיה מלך יהודה העתיקו את המשלים שהיו ידועים בעל-פה אל הכתב, </p><p>הלא העתקה זה העברת הכתב ממקומו [בימים ההם מגנזי שלמה] לספר חדש לספר משלי שלמה</p>',
		'115934331744503251746',
		'Daian Moshe',
		'2016-01-13 14:52:02'
		)
		;

