		INSERT INTO tguvot (parent,body,userid,username,created_at) VALUES (
		'tnk1/messages/new_1.html',
		'<p>שַׁרְשֶׁרֶת הדורות מעידה כי פעם אתה בא בדור שר ואחריך דור רש ואחריו דור שר וכולם קשורים בשַׁרְשֶׁרֶת</p>',
