		INSERT INTO tguvot (parent,body,userid,username,created_at) VALUES (
		'tnk1/messages/prqim_t0101_23.html',
		'<p>דיין שם , לדעתך חגי התורה חלים בתקופה שאין בית מקדש ? איך אפשר לקיים את חגי ה\' אם אין קורבנות ואין אפילו את הידע מתי לחוג כל חג?</p><p></p>',
		'112528711781981336347',
		'אסף מלול',
		'2018-05-20 18:00:10'
		)
		;

