		INSERT INTO tguvot (parent,body,userid,username,created_at) VALUES (
		'tnk1/tora/brejit/rqia_hjmym.html',
		'<p>יש טוענים כי הרקיע זה בכלל המעטפת של הDume כלומר אנחנו חיים בעולם שטוח שמעליו כיפת רקיע.</p><p>בשנות השישים לארה&quot;ב היה פרוייקט שקראו לו פישבול שמטרתו הייתה לבדוק את עמידות הכיפה. </p><p></p>',
		'114836369812064860809',
		'85 Aviran',
		'2018-03-03 10:03:09'
		)
		;

